CREATE TABLE camera(camera_id SERIAL, brand VARCHAR, model VARCHAR, PRIMARY KEY(camera_id));
CREATE TABLE price(price_id SERIAL, price VARCHAR, currency VARCHAR, PRIMARY KEY(price_id));
CREATE TABLE address(address_id SERIAL, street VARCHAR, city VARCHAR, state VARCHAR, postal_code VARCHAR, country VARCHAR, PRIMARY KEY(address_id));
CREATE TABLE person(person_id SERIAL, address_id INT, first_name VARCHAR, last_name VARCHAR, PRIMARY KEY(person_id), CONSTRAINT fk_address FOREIGN KEY(address_id) REFERENCES address(address_id));
CREATE TABLE photo(photo_id SERIAL, price_id INT, author_id INT, photo_details VARCHAR, camera_id INT, resolution VARCHAR, place_of_photo VARCHAR, category VARCHAR, PRIMARY KEY(photo_id), CONSTRAINT fk_price FOREIGN KEY(price_id) REFERENCES price(price_id), CONSTRAINT fk_camera FOREIGN KEY(camera_id) REFERENCES camera(camera_id));
CREATE TABLE orders(order_id SERIAL, buyer_id INT, author_id INT, photo_id INT, price_id INT, order_method VARCHAR, PRIMARY KEY(order_id), CONSTRAINT fk_buyer FOREIGN KEY(buyer_id) REFERENCES person(person_id), CONSTRAINT fk_author FOREIGN KEY(author_id) REFERENCES person(person_id), FOREIGN KEY(price_id) REFERENCES price(price_id));